import { authSuccess } from "../actions/auth"

const initialState = {
  token: null
}

export default function authReducer(state = initialState, action) {
  switch (action.type) {
    case authSuccess:
      return {
        ...state, token: action.token
      }
    default: 
    return state
  }
}
